// LOGIC CORE FINAL (Schedule Enforcement & Late Joiner Only)

var payload = msg.payload;
var topicParts = msg.topic.split('/');
var nodeID = topicParts[topicParts.length - 1];

// Fallback jika topik tidak valid
if (!nodeID || nodeID === "uplink") nodeID = "001";

// 1. INPUT HANDLING
var strInput = (Buffer.isBuffer(payload)) ? payload.toString('utf8') : payload.toString();
strInput = strInput.trim();

// 2. HEADER STRIPPING
var lastComma = strInput.lastIndexOf(',');
var cleanStr = (lastComma !== -1) ? strInput.substring(lastComma + 1).trim() : strInput;
var msgContent = cleanStr.replace(/<.*?>/, "").trim();

// 3. INIT STATUS
var statusMap = global.get('seiscatch_status') || {};
if (!statusMap[nodeID]) {
    statusMap[nodeID] = { vcc: 0, status: "Online", time: Date.now(), id: nodeID };
}

// =================================================
// JALUR A: DATA STATUS (TEKS)
// =================================================
if (msgContent.includes("stat=") || msgContent.includes("sys=")) {

    // A.1 Start Session (T0)
    if (msgContent.includes("stat=SESSION")) {
        var t0Match = msgContent.match(/t0=(\d+)/);
        if (t0Match) {
            var t0_val = parseInt(t0Match[1]);
            global.set('session_t0_' + nodeID, t0_val * 1000);
            statusMap[nodeID].status = "Session Started";
        }
    }

    // A.2 Status READY
    if (msgContent.includes("stat=READY")) {
        statusMap[nodeID].status = "READY (Waiting Fetch)";
    }

    // A.3 Update VCC
    if (msgContent.includes("sys=")) {
        var match = msgContent.match(/sys=(\d+)/);
        if (match) statusMap[nodeID].vcc = parseInt(match[1]);
        statusMap[nodeID].status = "Idle / Polling";
    }
    statusMap[nodeID].time = Date.now();

    // A.4 Handshake Waktu (TIME_REQ)
    // WAJIB: Wemos butuh waktu server agar bisa menghitung mundur jadwal ACQ_AT
    if (msgContent.includes("stat=TIME_REQ")) {
        var now = Math.floor(Date.now() / 1000);
        setTimeout(function () {
            var dlPayload = "<" + nodeID + ">TIME:" + now;
            node.send([null, null, { payload: dlPayload, topic: "seismic/downlink/" }]);
        }, 1200);
        statusMap[nodeID].status = "Syncing Time";
    }

    // A.5 Cek Tugas (POLL) - LOGIKA BARU (STRICT SCHEDULE)
    if (msgContent.includes("stat=POLL")) {

        // Cek apakah ada jadwal aktif di masa depan?
        var targetTime = global.get('acq_target_time');
        var nowSec = Math.floor(Date.now() / 1000);

        // Hanya kirim perintah jika Target Time > Waktu Sekarang
        if (targetTime && targetTime > nowSec) {
            var timeLeft = targetTime - nowSec;

            // Jangan kirim jika sisa waktu terlalu mepet (< 5 detik)
            if (timeLeft > 5) {
                // node.warn("Node " + nodeID + " Late Join. Sending Schedule.");
                setTimeout(function () {
                    // Kirim Jadwal (ACQ_AT)
                    var dlPayload = "<" + "BROADCAST" + ">ACQ_AT:" + targetTime;
                    node.send([null, null, { payload: dlPayload, topic: "seismic/downlink/" }]);
                }, 1200);
                statusMap[nodeID].status = "Scheduled ( in " + timeLeft + "s )";
            } else {
                statusMap[nodeID].status = "Too Late for Schedule";
            }
        }
        else {
            // Jika tidak ada jadwal masa depan, biarkan IDLE.
            // JANGAN PERNAH KIRIM ACQ_START DISINI LAGI.
            statusMap[nodeID].status = "Idle (No Schedule)";
        }
    }

    global.set('seiscatch_status', statusMap);
    node.send([{ payload: statusMap }, null, null]);
    return null;
}

// =================================================
// JALUR B: DATA SEISMIK (HEX STRING)
// =================================================
else if (msgContent.length > 20) {
    statusMap[nodeID].status = "Receiving Data";
    statusMap[nodeID].time = Date.now();
    global.set('seiscatch_status', statusMap);
    node.send([{ payload: statusMap }, null, null]);

    try {
        var dataBuffer = Buffer.from(msgContent, 'hex');
        if (dataBuffer.length > 0) {
            var msgBiner = { payload: dataBuffer, topic: nodeID };
            node.send([null, msgBiner, null]);
        }
    } catch (e) { return null; }
}

return null;

// BINARY PARSER FINAL (Clean Version)
// Input: Buffer [Seq (1 Byte), Data (Int16)...]
// Header <ID> diasumsikan sudah dibuang oleh Logic Core.

var buf = msg.payload;
var nodeID = msg.topic;

// 1. Validasi Dasar
// Minimal harus ada 1 byte Seq + 2 byte Data (1 sampel) = 3 byte
if (!Buffer.isBuffer(buf) || buf.length < 3) return null;

// 2. Ambil Sequence (Pasti di Byte ke-0)
var seqNum = buf[0];

// 3. Ambil Data Seismik (Mulai dari Byte ke-1)
var seismicData = buf.slice(1); 

var points = [];

// 4. Decoding Int16 Little Endian
// Loop sepanjang data yang tersisa
for (var i = 0; i < seismicData.length; i += 2) {
    // Pastikan pasangan byte lengkap (Low + High)
    if (i + 1 < seismicData.length) {
        var val = seismicData.readInt16LE(i);
        points.push(val);
    }
}

// 5. Output
msg.payload = points;
msg.topic = nodeID;
msg.packet_seq = seqNum; // Sertakan untuk debugging / perhitungan waktu DB

// Info debug panjang data
msg.data_len = seismicData.length; 

return msg;

// FILTER PENGAMAN:
// Hanya proses jika payload adalah STRING (ID Node, misal "001")
// Abaikan jika payload adalah Object (Data update status)
if (typeof msg.payload !== 'string') {
    return null;
}

// 1. Set Global Variable
global.set('selected_view_node', msg.payload);

// 2. Notifikasi UI
var notifMsg = {payload: "Memuat data Node: " + msg.payload};

// 3. Redirect ke Tab Analisis
var navMsg = {payload: {tab: "PLOT & ANALISIS"}};

return [notifMsg, navMsg];

// BUILD QUERY: SESSION AWARE (FIXED)

// 1. Ambil Node ID yang dipilih
var selectedNode = global.get('selected_view_node');

if (!selectedNode) {
    node.warn("⚠️ Tidak ada node yang dipilih untuk didownload.");
    return null;
}

// 2. Ambil Waktu Mulai Sesi Terakhir (T0) dari Global Variable
// Variabel ini dibuat oleh Logic Core saat menerima "stat=SESSION"
var sessionStartMs = global.get('session_t0_' + selectedNode);

var query = "";

// 3. Logika Pemilihan Data
if (sessionStartMs) {
    // SKENARIO UTAMA: Ada data sesi aktif
    // Ambil data di mana waktu >= Waktu Mulai Sesi
    // Kita urutkan ASC (Lama ke Baru) agar CSV rapi dari detik 0 ke detik 5.

    // Note: InfluxQL butuh suffix 'ms' jika kita pakai epoch milliseconds
    query = `SELECT amplitude FROM seismic_raw WHERE "node_id" = '${selectedNode}' AND time >= ${sessionStartMs}ms ORDER BY time ASC`;

    node.warn("Downloading Session starting at: " + new Date(sessionStartMs).toLocaleString());
}
else {
    // SKENARIO CADANGAN: Jika T0 tidak ada (misal baru restart Node-RED)
    // Ambil 1000 data terakhir saja sebagai fallback

    query = `SELECT amplitude FROM seismic_raw WHERE "node_id" = '${selectedNode}' ORDER BY time DESC LIMIT 1000`;

    node.warn("⚠️ Warning: T0 tidak ditemukan. Mengambil 1000 data terakhir.");
}

msg.query = query;
return msg;

// TO CSV (Fixed Node ID Logic)

var data = msg.payload;
// 1. Ambil ID Node langsung dari Pilihan User (Pasti Benar)
var currentNode = global.get('selected_view_node') || "unknown";

// Validasi Data
if (!Array.isArray(data) || data.length === 0) {
    return null; // Jangan lanjutkan jika kosong
}

// Header CSV
var csv = "timestamp,node_id,amplitude\n";

// Loop Data
for (var i = 0; i < data.length; i++) {
    var row = data[i];

    // Kita gunakan 'currentNode' yang kita ambil dari Global
    // row.amplitude adalah nilai getaran dari database
    csv += `${row.time},${currentNode},${row.amplitude}\n`;
}

msg.payload = csv;

// Nama File: seismic_001_timestamp.csv
var timeStr = new Date().toISOString().replace(/[:.]/g, "-").slice(0, 19);
msg.filename = `seismic_${currentNode}_${timeStr}.csv`;

return msg;

// FORMATTER DB (Absolute Time Calculation)
var values = msg.payload;
var nodeID = msg.topic || "001";
var seqNum = msg.packet_seq;

if (!Array.isArray(values)) return null;
if (seqNum === undefined) seqNum = 0; // Safety

var batch = [];

// Ambil T0 dari Global
var t0_epoch_ms = global.get('session_t0_' + nodeID);

// Fallback jika T0 tidak ada (pakai waktu sekarang)
if (!t0_epoch_ms) t0_epoch_ms = Date.now();

// Parameter Sampling
var intervalMs = 10; // 100Hz = 10ms
var samplesPerPacket = 30; // Sesuaikan dengan chunk Wemos (60 byte / 2)

// Hitung Waktu Awal Paket Ini
var packetStartTimeMs = t0_epoch_ms + (seqNum * samplesPerPacket * intervalMs);

for (var i = 0; i < values.length; i++) {
    var absoluteTime = packetStartTimeMs + (i * intervalMs);

    batch.push({
        measurement: "seismic_raw",
        tags: { node_id: nodeID.toString() },
        fields: { amplitude: parseInt(values[i]) },
        timestamp: new Date(absoluteTime) // Object Date
    });
}

msg.payload = batch;
return msg;

// CHART FORMATTER (Time Series Dedicated)
// Menangani: 
// 1. APPEND_DATA (Realtime dari Dispatcher)
// 2. REPLACE_DATA (History dari Database)

var inputData = msg.payload;
var outputMsg = {};
var labels = [];
var values = [];

// Validasi: Data harus Array
if (!Array.isArray(inputData)) return null;

// --- KASUS A: HISTORY REPLAY (REPLACE) ---
// Ciri: Topik khusus 'HISTORY_REPLAY' (dari node Format History)
if (msg.topic === "HISTORY_REPLAY") {

    // Data history formatnya: [{x: time, y: amp}, ...]
    // Kita perlu pisahkan untuk Chart.js
    var histData = inputData[0]; // Biasanya dibungkus array luar oleh Format History

    // Jika formatnya belum terbungkus array luar, pakai langsung
    if (!Array.isArray(histData)) histData = inputData;

    labels = histData.map(function(d) { return new Date(d.x).toLocaleTimeString(); });
    values = histData.map(function(d) { return d.y; });

    outputMsg.topic = "REPLACE_DATA";
    outputMsg.payload = { 
        data: values, 
        labels: labels, // Label waktu real untuk history
        isHistory: true 
    };
    return outputMsg;
}

// --- KASUS B: REALTIME STREAMING (APPEND) ---
// Ciri: Array Angka Murni (Integer) dari Dispatcher
else {
    // Ambil Start Index dari Binary Parser (agar sumbu X jalan terus 100, 101...)
    var startIndex = msg.start_index; 
    if (startIndex === undefined) startIndex = 0;

    labels = [];
    values = inputData;

    // Buat label urut (Continuous Counter)
    for(var i=0; i<inputData.length; i++) {
        labels.push(startIndex + i);
    }

    outputMsg.topic = "APPEND_DATA";
    outputMsg.payload = { 
        data: values,
        labels: labels 
    };
    return outputMsg;
}

// BUILD QUERY (Session Filter)
var selectedNode = global.get('selected_view_node');
if (!selectedNode) {
    node.warn("Pilih Node dulu");
    return null;
}

var sessionStartMs = global.get('session_t0_' + selectedNode);
var query = "";

if (sessionStartMs) {
    // Ambil data sesi ini saja
    query = `SELECT amplitude FROM seismic_raw WHERE "node_id" = '${selectedNode}' AND time >= ${sessionStartMs}ms ORDER BY time ASC`;
} else {
    // Fallback
    query = `SELECT amplitude FROM seismic_raw WHERE "node_id" = '${selectedNode}' ORDER BY time DESC LIMIT 1000`;
}

msg.query = query;
return msg;

var data = msg.payload;
if (!Array.isArray(data) || data.length === 0) return null;

var values = [];
// Ganti jadi loop MAJU (i++)
for (var i = 0; i < data.length; i++) {
    values.push(data[i].amplitude);
}

// Kirim sebagai JSON String ke Python
msg.payload = JSON.stringify(values);
return msg;

try {
    var result = JSON.parse(msg.payload);

    if (result.status === "OK" && result.fft) {
        var labels = [];
        var values = [];

        // Pisahkan X (Freq) dan Y (Amp) untuk Chart.js
        for(var i=0; i<result.fft.length; i++) {
            labels.push(result.fft[i].x);
            values.push(result.fft[i].y);
        }

        msg.payload = {
            labels: labels,
            data: values
        };
        msg.topic = "UPDATE_FFT";
        return msg;
    }
} catch(e) { 
    node.warn("Error Parse Python: " + e);
    return null; 
}
return null;

// LOGIKA PROGRESS BAR (Kalibrasi 50 Sampel/Paket)

// Total Sampel: 500 (5 detik)
// Per Paket: 50 Sampel (sesuai debug)
// Total Paket: 500 / 50 = 10
var totalPackets = 34; 

var currentCount = flow.get('pkg_count') || 0;

// 1. Trigger Reset (Dari Tombol Global ACQ)
// Topik bisa 'trigger_acq' atau 'RESET_PROGRESS' tergantung node sebelumnya
if (msg.topic === "trigger_acq" || msg.topic === "RESET_PROGRESS") {
    flow.set('pkg_count', 0);
    return { payload: 0, topic: "Menunggu..." };
}

// 2. Hitung Progress (Dari Data Masuk)
// Pastikan hanya menghitung jika payload adalah array data seismik
if (Array.isArray(msg.payload) && msg.payload.length > 0) {
    currentCount++;
    flow.set('pkg_count', currentCount);

    // Hitung Persentase
    var percent = Math.round((currentCount / totalPackets) * 100);

    // Safety Cap
    if (percent > 100) percent = 100;

    msg.payload = percent;
    msg.topic = "Menerima: " + currentCount + "/" + totalPackets + " pkt";

    return msg;
}

return null; // Abaikan pesan lain

// BUILD QUERY (Session Filter)
var selectedNode = global.get('selected_view_node');
if (!selectedNode) {
    node.warn("Pilih Node dulu");
    return null;
}

var sessionStartMs = global.get('session_t0_' + selectedNode);
var query = "";

if (sessionStartMs) {
    // Ambil data sesi ini saja
    query = `SELECT amplitude FROM seismic_raw WHERE "node_id" = '${selectedNode}' AND time >= ${sessionStartMs}ms ORDER BY time ASC`;
} else {
    // Fallback
    query = `SELECT amplitude FROM seismic_raw WHERE "node_id" = '${selectedNode}' ORDER BY time DESC LIMIT 1000`;
}

msg.query = query;
return msg;

// FORMAT HISTORY (Fixed & Optimized)
// Mencegah Crash akibat Loop ganda

var data = msg.payload;

// 1. Validasi
if (!Array.isArray(data) || data.length === 0) {
    return null;
}

// 2. Transformasi Data (Mapping)
// Kita gunakan method .map() yang jauh lebih cepat dan aman daripada for-loop manual
// Karena Query SQL sudah "ORDER BY time ASC", urutannya sudah benar.

var chartData = data.map(function (row) {
    return {
        x: new Date(row.time).getTime(), // Konversi ISO String ke Epoch MS
        y: row.amplitude                 // Nilai Amplitudo
    };
});

// 3. Output
// Bungkus dalam array lagi sesuai standar Chart.js wrapper kita
msg.payload = [chartData];
msg.topic = "HISTORY_REPLAY";

return msg;

// FASE 1: BROADCAST & RESET

// A. Hitung Waktu Target
var delaySeconds = 5;
var targetTime = Math.floor(Date.now() / 1000) + delaySeconds;

// Simpan ke Global
global.set('acq_target_time', targetTime);

// B. Siapkan Pesan Command (Output 1)
var cmdMsg = {
    payload: "<BROADCAST>ACQ_AT:" + targetTime,
    topic: "seismic/downlink/"
};

// C. Siapkan Pesan Reset (Output 2)
var resetMsg = {
    topic: "RESET_PROGRESS",
    payload: 0
};

node.warn("Siklus Dimulai. Target: " + targetTime);

// Kirim ke masing-masing output
return [cmdMsg, resetMsg];

// FASE 2: BUAT DAFTAR ANTRIAN

// Ambil semua node yang pernah lapor diri
var statusMap = global.get('seiscatch_status') || {};
var nodeList = Object.keys(statusMap);

if (nodeList.length === 0) {
    node.warn("Tidak ada node aktif untuk di-fetch.");
    return null;
}

node.warn("Mulai mengambil data dari: " + JSON.stringify(nodeList));

// Simpan antrean ke flow variable
flow.set('fetch_queue', nodeList);
flow.set('is_fetching', true);

// Trigger loop pertama
return { payload: "NEXT" };

// FASE 3: LOOP PENGAMBILAN DATA

var queue = flow.get('fetch_queue') || [];

// A. Cek jika antrean habis
if (queue.length === 0) {
    flow.set('is_fetching', false);
    node.warn("Siklus Selesai. Semua node telah dipanggil.");
    global.set('global_command', 'STOP');
    return null;
}

// B. Ambil Node ID berikutnya
var nextNode = queue.shift();
flow.set('fetch_queue', queue);

node.warn("Memanggil Node: " + nextNode);

// C. Susun Pesan MQTT (Topik Umum, Payload Bertarget)
var mqttMsg = {
    // Payload Bertarget (misal: <SEISMIC_NODE_001>FETCH)
    payload: "<" + nextNode + ">FETCH", 
    // Topik Umum (mematuhi hasil test: topic tanpa ID tambahan)
    topic: "seismic/downlink/"
};

// Output Notifikasi UI
var uiMsg = { payload: "Mengambil Data Node: " + nextNode };

// Output 1: Ke MQTT Out & Delay Loop
// Output 2: Ke Notifikasi UI
return [mqttMsg, uiMsg];
